// Property Search API Endpoint
app.get('/api/properties/search', async (req, res) => {
  try {
    const { location, minPrice, maxPrice, type } = req.query;

    const filters = {};
    if (location) filters.location = { $regex: location, $options: 'i' };
    if (minPrice) filters.price = { $gte: parseInt(minPrice) };
    if (maxPrice) filters.price = { ...filters.price, $lte: parseInt(maxPrice) };
    if (type) filters.propertyType = type;

    const properties = await Property.find(filters)
      .sort({ createdAt: -1 })
      .limit(20);

    res.json({ success: true, data: properties });
  } catch (error) {
    res.status(500).json({ success: false, error: error.message });
  }
});

// Custom hook for real-time analytics data
import { useEffect, useState } from 'react';
import { ref, onValue } from 'firebase/database';
import { database } from '@/lib/firebase';

interface AnalyticsData {
  revenue: number;
  orders: number;
  visitors: number;
  conversionRate: number;
}

export const useRealtimeAnalytics = (storeId: string) => {
  const [data, setData] = useState<AnalyticsData | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const analyticsRef = ref(database, `analytics/${storeId}`);

    const unsubscribe = onValue(analyticsRef, (snapshot) => {
      const analyticsData = snapshot.val();
      setData(analyticsData);
      setLoading(false);
    });

    return () => unsubscribe();
  }, [storeId]);

  return { data, loading };
};
